def combien
	puts "Il y a #{$journalistes.size} handles dans notre liste"
	return $journalistes.size
end

def unite
	$journalistes
	compte = 0

	$journalistes.each do |journaliste|
		if journaliste.index /[0-9]/
			compte += 1
		end
	end
	puts "il y a #{compte} journalistes avec un nombre en chiffre dans leur handle"
	return compte
end

def aude
	$journalistes
	compte = 0
	$journalistes.each do |journaliste|
		minuscule = journaliste.downcase
			if minuscule.match("aude")
				compte += 1
				
			end
	end
	puts "il y a #{compte} handle avec les lettre aude dans cet ordre dans cette liste"
	return compte
end

def majstart
	$journalistes
	compte = 0

	$journalistes.each do |journaliste|
		if journaliste.index /@[A-Z]/
			compte += 1
		end
	end
	puts "il y a #{compte} journalistes avec un handle qui commence avec une majuscule"
	return compte
end

def maj
	$journalistes
	compte = 0

	$journalistes.each do |journaliste|
		if journaliste.index /[A-Z]/
			compte += 1
		end
	end
	puts "il y a #{compte} journalistes avec un handle avec au moins une majuscule"
	return compte
end

def underscore
	$journalistes
	compte = 0
	$journalistes.each do |journaliste|
		caractere = journaliste.split ""
		caractere.each do |i|
			if i.match("_")
				compte += 1
				
			end
		end
	end
	puts "il y a #{compte} fois le symbole underscore dans cette liste"
	return compte
end

def ordrealphabetique
	$journalistes	
	puts $journalistes.sort(&:casecmp)
end

def cinquantelesmoinslong
	$journalistes
	puts $journalistes.sort_by(&:length).first(50)
end

def ouestcharlie
	$journalistes
	puts "L'handle d'epenser est à la place #{$journalistes.index("@epenser") + 1}."
end

